import math

def add(number1 , number2):
    return number1  + number2

def remove(number1 , number2):
    return number1  - number2

def multiply(number1 , number2):
    return number1 * number2

def divide(number1 , number2):
    return number1 / number2

def sin(number):
    return math.sin(number)

def cos(number):
    return math.cos(number)

def tan(number):
    return math.tan(number)